import { useState } from 'react'

interface AccordionItem {
  title: string
  content: string
}

interface AccordionProps {
  title: string
  items: AccordionItem[]
}

function Accordion({ title, items }: AccordionProps) {
  const [openIndex, setOpenIndex] = useState<number | null>(null)

  const toggle = (index: number) => {
    setOpenIndex(openIndex === index ? null : index)
  }

  return (
    <div className="flex flex-col gap-2">
      <h2 className="text-lg font-bold text-chocolate mb-2">{title}</h2>
      {items.map((item, index) => (
        <div 
          key={index}
          className="bg-white rounded-xl overflow-hidden shadow-soft"
        >
          <button
            onClick={() => toggle(index)}
            className="w-full px-4 py-3 flex items-center justify-between text-left hover:bg-chocolate/5 transition-colors"
          >
            <span className="text-sm font-medium text-chocolate pr-4">{item.title}</span>
            <span 
              className={`material-symbols-outlined text-chocolate/50 text-[20px] transition-transform duration-300 flex-shrink-0 ${
                openIndex === index ? 'rotate-180' : ''
              }`}
            >
              expand_more
            </span>
          </button>
          <div 
            className={`overflow-hidden transition-all duration-300 ${
              openIndex === index ? 'max-h-[2000px]' : 'max-h-0'
            }`}
          >
            <div className="px-4 pb-4 text-xs text-chocolate/70 leading-relaxed whitespace-pre-wrap">
              {item.content}
            </div>
          </div>
        </div>
      ))}
    </div>
  )
}

